package uz.gita.users_app.data.repositoryimport android.util.Logimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.flow.Flowimport kotlinx.coroutines.flow.catchimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.flowimport kotlinx.coroutines.flow.flowOnimport kotlinx.coroutines.flow.onEachimport uz.gita.users_app.data.mapper.toEntityimport uz.gita.users_app.data.mapper.toModelimport uz.gita.users_app.data.model.UsersModelimport uz.gita.users_app.data.source.local.room.dao.UsersDaoimport uz.gita.users_app.data.source.remote.api.UsersApiimport uz.gita.users_app.domain.repository.UsersRepositoryimport javax.inject.Injectclass UsersRepositoryImpl @Inject constructor(    private val api: UsersApi,    private val dao: UsersDao,) : UsersRepository {    override fun getAllUser(): Flow<List<UsersModel>> = flow {        Log.d("TTT", "getAllUser: 1")        val response = api.getAllUsers()        if (response.isSuccessful && response.body() != null) {            response.body()?.let {                dao.insertUsers(response.body()!!.results.map { it.toEntity() })                emit(response.body()!!.results.map {                    it.toModel()                })            }        }    }        .catch {            dao.getAllUsers()                .onEach { emit(it.map { it.toModel() }) }.collect()        }        .flowOn(Dispatchers.IO)}