package uz.gita.users_app.data.source.local.room.daoimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport kotlinx.coroutines.flow.Flowimport uz.gita.users_app.data.source.local.room.entity.UsersEntity@Daointerface UsersDao {    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertUsers(usersEntity: List<UsersEntity>)    @Query("SELECT * FROM UsersEntity")    fun getAllUsers(): Flow<List<UsersEntity>>}