package uz.gita.users_app.data.source.remote.apiimport retrofit2.Responseimport retrofit2.http.GETimport retrofit2.http.Queryimport uz.gita.users_app.data.source.remote.response.UsersResponseimport uz.gita.users_app.utils.Constants.USERS_COUNTinterface UsersApi {    @GET("api/")    suspend fun getAllUsers(        @Query("results") count: Int = USERS_COUNT,    ): Response<UsersResponse>}