package uz.gita.users_app.diimport android.content.Contextimport androidx.room.Roomimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport uz.gita.users_app.data.source.local.database.UsersDatabaseimport uz.gita.users_app.data.source.local.room.dao.UsersDaoimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class DatabaseModule {    @[Provides Singleton]    fun providesDatabase(@ApplicationContext context: Context): UsersDatabase =        Room.databaseBuilder(context, UsersDatabase::class.java, "users.db").build()    @[Provides Singleton]    fun provideDao(usersDatabase: UsersDatabase): UsersDao = usersDatabase.getUsersDao()}