package uz.gita.users_app.presenter.screens.detailimport androidx.lifecycle.ViewModelimport dagger.hilt.android.lifecycle.HiltViewModelimport kotlinx.coroutines.flow.MutableStateFlowimport kotlinx.coroutines.flow.updateimport javax.inject.Inject@HiltViewModelclass DetailViewModel @Inject constructor() : DetailContract.ViewModel, ViewModel() {    override val uiState = MutableStateFlow(DetailContract.UIState())    override fun onEventDispatcher(intent: DetailContract.Intent) {        when(intent) {            is DetailContract.Intent.setData -> {                uiState.update { it.copy(data = intent.data) }            }        }    }    init {    }}